import { Component  } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Watch } from '../shared/watch';
import { WatchService } from '../shared/watch.service';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watchForm: FormGroup;
  watches: Watch[];
  cartItems: Watch[] = [];

  constructor(private formBuilder: FormBuilder, private watchService: WatchService) {
    this.createForm();
    this.watches = this.watchService.getWatches();
  }

  createForm() {
    this.watchForm = this.formBuilder.group({
      name: ['', Validators.required],
      brand: ['', Validators.required],
      price: ['', Validators.required],
      image: ['', Validators.required]
    });
  }

  onSubmit() {
    const watch: Watch = {
      name: this.watchForm.value.name,
      brand: this.watchForm.value.brand,
      price: this.watchForm.value.price,
      image: this.watchForm.value.image
    };
    this.watchService.addWatch(watch);
    this.watches = this.watchService.getWatches();
    this.watchForm.reset();
  }

  addToCart(watch: Watch) {
    const itemIndex = this.cartItems.findIndex(item => item.name === watch.name);
    if (itemIndex === -1) {
      this.cartItems.push({...watch, quantity: 1});
    } else {
      this.cartItems[itemIndex].quantity++;
    }
  }

  removeFromCart(watch: Watch) {
    const itemIndex = this.cartItems.findIndex(item => item.name === watch.name);
    if (itemIndex !== -1) {
      this.cartItems[itemIndex].quantity--;
      if (this.cartItems[itemIndex].quantity === 0) {
        this.cartItems.splice(itemIndex, 1);
      }
    }
  }

  getTotal() {
    return this.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  }
}
