import { Component  } from '@angular/core';


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  products: Product[] = [
    { id: 1, name: 'Watch 1', price: 100, description: 'This is a nice watch.' },
    { id: 2, name: 'Watch 2', price: 200, description: 'This is a fancy watch.' },
    { id: 3, name: 'Watch 3', price: 300, description: 'This is a luxury watch.' }
  ];
  selectedProduct: Product;

  onSelect(product: Product) {
    this.selectedProduct = product;
  }

  onDelete(product: Product) {
    const index = this.products.findIndex(p => p.id === product.id);
    if (index !== -1) {
      this.products.splice(index, 1);
      this.selectedProduct = null;
    }
}
}
