import { Component} from '@angular/core';


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watches = [
    { id: 1, name: 'Đồng hồ 1', price: 100, image: 'watch1.jpg' },
    { id: 2, name: 'Đồng hồ 2', price: 200, image: 'watch2.jpg' },
    { id: 3, name: 'Đồng hồ 3', price: 300, image: 'watch3.jpg' }
  ];
  newWatch = { id: 0, name: '', price: 0, image: '' };
  selectedWatch = null;

  addWatch() {
    // Kiểm tra xem đã nhập đầy đủ thông tin sản phẩm mới hay chưa
    if (this.newWatch.name.trim() === '' || this.newWatch.price === 0 || this.newWatch.image.trim() === '') {
      alert('Vui lòng nhập đầy đủ thông tin sản phẩm mới!');
      return;
    }

    // Tạo id cho sản phẩm mới
    this.newWatch.id = this.watches.length > 0 ? this.watches[this.watches.length - 1].id + 1 : 1;
    this.watches.push(this.newWatch);

    // Reset form và đối tượng newWatch
    this.newWatch = { id: 0, name: '', price: 0, image: '' };
  }

  selectWatch(watch) {
    this.selectedWatch = watch;
  }

  updateWatch() {
    // Kiểm tra xem đã nhập đầy đủ thông tin sản phẩm cần sửa hay chưa
    if (this.selectedWatch.name.trim() === '' || this.selectedWatch.price === 0 || this.selectedWatch.image.trim() === '') {
      alert('Vui lòng nhập đầy đủ thông tin sản phẩm!');
      return;
    }

    // Tìm sản phẩm cần sửa trong mảng và cập nhật thông tin mới
    const index = this.watches.findIndex(item => item.id === this.selectedWatch.id);
    if (index >= 0) {
      this.watches[index].name = this.selectedWatch.name;
      this.watches[index].price = this.selectedWatch.price;
      this.watches[index].image = this.selectedWatch.image;
    }

    // Reset đối tượng selectedWatch
    this.selectedWatch = null;
  }

  deleteWatch(watch) {
    // Xác nhận xóa sản phẩm
    if (confirm(`Bạn có chắc muốn xóa sản phẩm ${watch.name}?`)) {
      // Tìm sản phẩm cần xóa trong mảng và xóa sản phẩm đó
      const index = this.watches.findIndex(item => item.id === watch.id);
      if (index >= 0) {
        this.watches.splice(index, 1);
      }

      // Reset đối tượng selectedWatch
      this.selectedWatch = null;
    }
  }
}
