import { Component } from '@angular/core';



@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  @Output() addWatch = new EventEmitter<Watch>();

  onSubmit(form: HTMLFormElement) {
    const brand = form.brand.value;
    const model = form.model.value;
    const price = parseFloat(form.price.value);
    const imageUrl = form['image-url'].value;

    const newWatch = new Watch(brand, model, price, imageUrl);

    form.reset();

    this.addWatch.emit(newWatch);
  }

}









<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
<script>
  class Watch {
    constructor(brand, model, price, imageUrl) {
      this.brand = brand;
      this.model = model;
      this.price = price;
      this.imageUrl = imageUrl;
    }
  }

  class WatchFormComponent {
    constructor() {
      this.form = document.querySelector('form');
      this.form.addEventListener('submit', this.onSubmit.bind(this));
    }

    onSubmit(event) {
      event.preventDefault();

      const brand = this.form.brand.value;
      const model = this.form.model.value;
      const price = parseFloat(this.form.price.value);
      const imageUrl = this.form['image-url'].value;

      const newWatch = new Watch(brand, model, price, imageUrl);

      this.form.reset();

      // emit new watch to parent component
    }
  }


</script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }








      form {
        display: flex;
        flex-direction: column;
        max-width: 500px;
        margin: 0 auto;
      }

      label {
        margin-top: 10px;
      }

      input {
        padding: 5px;
        border-radius: 5px;
        border: 1px solid #ccc;
        margin-top: 5px;
      }

      button {
        background-color: #007bff;
        color: #fff;
        border-radius: 5px;
        padding: 10px;
        border: none;
        margin-top: 10px;
        cursor: pointer;
      }

      button:hover {
        background-color: #0062cc;
      }
  </style>
</head>
<form>
  <label for="brand">Brand:</label>
  <input type="text" id="brand" name="brand" required>

  <label for="model">Model:</label>
  <input type="text" id="model" name="model" required>

  <label for="price">Price:</label>
  <input type="number" id="price" name="price" required min="0">

  <label for="image-url">Image URL:</label>
  <input type="url" id="image-url" name="image-url" required>

  <button type="submit">Add Watch</button>
</form>
<body>
<html>
