@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  products = [
    { id: 1, name: 'Đồng hồ Casio', price: 100 },
    { id: 2, name: 'Đồng hồ Citizen', price: 200 },
    { id: 3, name: 'Đồng hồ Seiko', price: 150 },
  ];

  selectedProduct = null;

