import { Component, OnInit, ViewChild, ElementRef } from '@angular/core';


@Component({
  selector: 'app-donhang',
  templateUrl: './donhang.component.html',
  styleUrls: ['./donhang.component.css']
})
export class DonhangComponent {
  orders: any[] = [
    { id: 1, customerName: 'Nguyễn Văn A', address: '123 Đường Trần Phú, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123456', orderDate: '2022-01-01' },
    { id: 2, customerName: 'Trần Thị B', address: '456 Đường Nguyễn Văn Linh, Quận Sơn Trà, TP Đà Nẵng', phoneNumber: '0905123457', orderDate: '2022-02-02' },
    { id: 3, customerName: 'Hoàng Văn C', address: '789 Đường Lê Duẩn, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123458', orderDate: '2022-03-03' },
{
  address: '246 Đường Hùng Vương, TP Huế', phoneNumber: '0905123459', orderDate: '2022-04-04' },
  { id: 5, customerName: 'Lê Văn E', address: '135 Đường Bà Triệu, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123460', orderDate: '2022-05-05' },
  { id: 6, customerName: 'Phạm Thị F', address: '246 Đường Hùng Vương, TP Huế', phoneNumber: '0905123461', orderDate: '2022-06-06' },
  { id: 7, customerName: 'Đỗ Văn G', address: '789 Đường Lê Duẩn, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123462', orderDate: '2022-07-07' },
  { id: 8, customerName: 'Nguyễn Thị H', address: '456 Đường Nguyễn Văn Linh, Quận Sơn Trà, TP Đà Nẵng', phoneNumber: '0905123463', orderDate: '2022-08-08' },
  { id: 9, customerName: 'Trần Văn I', address: '123 Đường Trần Phú, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123464', orderDate: '2022-09-09' },
  { id: 10, customerName: 'Lê Thị K', address: '135 Đường Bà Triệu, Quận Hải Châu, TP Đà Nẵng', phoneNumber: '0905123465', orderDate: '2022-10-10' }
];

newOrder: any = {};
selectedOrder: any = {};
searchTerm: string = '';

filteredOrders: any[] = [];

@ViewChild('addOrderModal') addOrderModal: ElementRef;
@ViewChild('orderDetailModal') orderDetailModal: ElementRef;
@ViewChild('editOrderModal') editOrderModal: ElementRef;

constructor() { }

ngOnInit() {
  this.filteredOrders = this.orders;
}

// Filter orders by customerName and address
search() {
  if (this.searchTerm === '') {
    this.filteredOrders = this.orders;
  } else {
    this.filteredOrders = this.orders.filter(order =>
      order.customerName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
      order.address.toLowerCase().includes(this.searchTerm.toLowerCase())
    );
  }
}

// Show add order modal
showAddOrderModal() {
  this.newOrder = {};
  this.addOrderModal.nativeElement.querySelector('#customerName').value = '';
  this.addOrderModal.nativeElement.querySelector('#address').value = '';
  this.addOrderModal.nativeElement.querySelector('#phoneNumber').value = '';
  this.addOrderModal.nativeElement.querySelector('#orderDate').value = '';
  $(this.addOrderModal.nativeElement).modal('show');
}

// Add new order
addOrder() {
  this.newOrder.id = this.orders.length + 1;
  this.orders.push(this.newOrder);
  this.filteredOrders = this.orders;
  $(this.addOrderModal.nativeElement).modal('hide');
}

// Show order detail modal
showOrderDetailModal(order: any) {
  this.selectedOrder = order;
  $(this.orderDetailModal.nativeElement).modal('show');
}

// Show edit order modal
showEditOrderModal(order: any) {
  this.selectedOrder = { ...order };
  $(this.editOrderModal.nativeElement).modal('show');
}

// Update order
updateOrder() {
  const index = this.orders.findIndex(order => order.id === this.selectedOrder.id);
  this.orders[index] = this.selectedOrder;
  this.filteredOrders = [...this.orders];
  $(this.editOrderModal.nativeElement).modal('hide');
}

// Delete order
deleteOrder(order: any) {
  const index = this.orders.findIndex(o => o.id === order.id);
  this.orders.splice(index, 1);
  this.filteredOrders = [...this.orders];
}
}  




}
