import { Component } from '@angular/core';

@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products = [
    { id: 1, name: 'Product 1', description: 'Description 1', price: 10 },
    { id: 2, name: 'Product 2', description: 'Description 2', price: 20 },
    { id: 3, name: 'Product 3', description: 'Description 3', price: 30 },
    { id: 4, name: 'Product 4', description: 'Description 4', price: 40 },
    { id: 5, name: 'Product 5', description: 'Description 5', price: 50 },
  ];

  editProduct(product: any) {
    // handle edit product logic here
  }

  deleteProduct(product: any) {
    // handle delete product logic here
  }
}
