import { Component } from '@angular/core';

@Component({
  selector: 'app-hinhanh',
  templateUrl: './hinhanh.component.html',
  styleUrls: ['./hinhanh.component.css']
})
export class HinhanhComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>


    .container {
      max-width: 500px;
      margin: 50px auto;
      background-color: #fff;
      padding: 20px;
      border-radius: 5px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
    }

    h1 {
      text-align: center;
      margin-bottom: 20px;
    }

    form {
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    input[type="submit"] {
      background-color: #4CAF50;
      color: white;
      padding: 12px 20px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
      margin-top: 20px;
    }

    input[type="submit"]:hover {
      background-color: #45a049;
    }
  </style>

</head>
<body>



  <div class="container">
    <h1>Đăng xuất</h1>
    <form id="logout-form" action="#" method="post">
      <p>Bạn có chắc chắn muốn đăng xuất?</p>
      <input type="submit" value="Đăng xuất">
    </form>
  </div>

  <script>
    // Lấy form đăng xuất
    var logoutForm = document.getElementById("logout-form");

    // Xử lý khi người dùng submit form
    logoutForm.addEventListener("submit", function(event) {
      // Ngăn chặn trình duyệt gửi form đăng xuất lên server
      event.preventDefault();

      // Gửi yêu cầu đăng xuất đến server
      // ...

      // Chuyển người dùng về trang đăng nhập hoặc trang chủ của trang web
      window.location.href = "login.html";
    });
  </script>

</body>
</html>
