import { Component } from '@angular/core';



import { WatchService } from './watch.service';

interface Watch {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
}



@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {

  watches: Watch[] = [];

  constructor(private watchService: WatchService) {}

  ngOnInit() {
    this.watchService.getWatches().subscribe(
      (watches) => {
        this.watches = watches;
      },
      (error) => {
        console.log('Error getting watches: ', error);
      }
    );
  }

  goToAddWatch() {
    // chuyển hướng đến trang thêm sản phẩm đồng hồ
  }

  editWatch(watch: Watch) {
    // chuyển hướng đến trang sửa sản phẩm đồng hồ
  }

  deleteWatch(watch: Watch) {
    if (confirm(`Are you sure to delete ${watch.name}?`)) {
      this.watchService.deleteWatch(watch.id).subscribe(
        () => {
          this.watches = this.watches.filter((w) => w.id !== watch.id);
          console.log('Watch deleted: ', watch);
        },
        (error) => {
          console.log('Error deleting watch: ', error);
        }
      );
    }
}
