import { Component } from '@angular/core';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
    <Script>
        interface Product {
            id: number;
            code: string;
            name: string;
            quantity: number;
            status: string;
            price: number;
            category: string;
          }

          const productForm = document.getElementById('product-form') as HTMLFormElement;
          const productIdInput = document.getElementById('product-id') as HTMLInputElement;
          const productCodeInput = document.getElementById('product-code') as HTMLInputElement;
          const productNameInput = document.getElementById('product-name') as HTMLInputElement;
          const productQuantityInput = document.getElementById('product-quantity') as HTMLInputElement;
          const productStatusInput = document.getElementById('product-status') as HTMLSelectElement;
          const productPriceInput = document.getElementById('product-price') as HTMLInputElement;
          const productCategoryInput =document.getElementById('product-category') as HTMLInputElement;
          const productTableBody = document.getElementById('product-table-body') as HTMLTableSectionElement;
          const resetButton = document.getElementById('reset-button') as HTMLButtonElement;

          let products: Product[] = [];
          let editMode = false;
          let editProductId = -1;

          // Hàm hiển thị danh sách sản phẩm lên bảng
          function displayProducts() {
            productTableBody.innerHTML = '';
            products.forEach(product => {
              const row = document.createElement('tr');
              row.innerHTML = `
                <td>${product.code}</td>
                <td>${product.name}</td>
                <td>${product.quantity}</td>
                <td>${product.status}</td>
                <td>${product.price}</td>
                <td>${product.category}</td>
                <td>
                  <button class="edit-button" data-product-id="${product.id}">Sửa</button>
                  <button class="delete-button" data-product-id="${product.id}">Xóa</button>
                </td>
              `;
              productTableBody.appendChild(row);
            });
          }

          // Hàm thêm sản phẩm vào danh sách
          function addProduct() {
            const code = productCodeInput.value;
            const name = productNameInput.value;
            const quantity = parseInt(productQuantityInput.value);
            const status = productStatusInput.value;
            const price = parseInt(productPriceInput.value);
            const category = productCategoryInput.value;
            const newProduct: Product = {
              id: products.length + 1,
              code,
              name,
              quantity,
              status,
              price,
              category,
            };
            products.push(newProduct);
            displayProducts();
          }

          // Hàm xóa sản phẩm khỏi danh sách
          function deleteProduct(productId: number) {
            products = products.filter(product => product.id !== productId);
            displayProducts();
          }

          // Hàm sửa sản phẩm trong danh sách
          function editProduct(productId: number) {
            const productToEdit = products.find(product => product.id === productId);
            if (productToEdit) {
              productIdInput.value = productToEdit.id.toString();
              productCodeInput.value = productToEdit.code;
              productNameInput.value = productToEdit.name;
              productQuantityInput.value = productToEdit.quantity.toString();
              productStatusInput.value = productToEdit.status;
              productPriceInput.value = productToEdit.price.toString();
              productCategoryInput.value = productToEdit.category;
              editMode = true;
              editProductId = productId;
            }
          }

          // Hàm lưu sản phẩm đã sửa vào danh sách
          function saveEditedProduct() {
            const productToSave = products.find(product => product.id === editProductId);
            if (productToSave) {
              productToSave.code = productCodeInput.value;
              productToSave.name = productNameInput.value;
              productToSave.quantity = parseInt(productQuantityInput.value);
              productToSave.status = productStatusInput.value;
              productToSave.price = parseInt(productPriceInput.value);
              productToSave.category = productCategoryInput.value;
              displayProducts();
              resetForm();
            }
          }

          // Hàm reset form
          function resetForm() {
            productForm.reset();
            editMode = false;
            editProductId = -1;
          }

          // Xử lý sự kiện submit form
          productForm.addEventListener('submit', event => {
            event.preventDefault();
            if (editMode) {
              saveEditedProduct();
            } else {
              addProduct();
            }
            resetForm();
          });

          // Xử lý sự kiện click nút reset
          resetButton.addEventListener('click', resetForm);

          // Xử lý sự kiện click nút sửa hoặc xóa
          productTableBody.addEventListener('click', event => {
            const target = event.target as HTMLElement;
            if (target.classList.contains('edit-button')) {
              const productId = parseInt(target.getAttribute('data-product-id') || '');
              editProduct(productId);
            } else if (target.classList.contains('delete-button')) {
              const productId = parseInt(target.getAttribute('data-product-id') || '');
              deleteProduct(productId);
            }
          });

          // Hiển thị danh sách sản phẩm ban đầu
          displayProducts();

    </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        align-items: center;

        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
      .container {
        max-width: 600px;
        margin: 0 auto;
        padding: 20px;
        font-family: Arial, sans-serif;
      }

      h1 {
        text-align: center;
      }

      form {
        display: flex;
        flex-direction: column;
        gap: 10px;
        margin-bottom: 20px;
      }

      label {
        font-weight: bold;
      }

      input[type="text"],
      input[type="number"],
      select {
        padding: 5px;
        border-radius: 5px;
        border: 1px solid #ccc;
      }

      button[type="submit"],
      button[type="button"] {
        padding: 5px 10px;
        background-color: #4CAF50;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
      }

      button[type="submit"]:hover,
      button[type="button"]:hover {
        background-color: #3e8e41;
      }

      table {
        width: 100%;
        border-collapse: collapse;
      }

      th, td {
        padding: 10px;
        text-align: left;
        border-bottom: 1px solid #ddd;
      }

      th {
        background-color: #4CAF50;
        color: white;
      }

      .edit-button, .delete-button {
        padding: 5px 10px;
        background-color: #4CAF50;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
      }

      .edit-button:hover, .delete-button:hover {
        background-color: #3e8e41;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="container">
    <h1>Danh sách sản phẩm</h1>
    <form id="product-form">
      <input type="hidden" id="product-id">
      <label for="product-code">Mã sản phẩm:</label>
      <input type="text" id="product-code">
      <label for="product-name">Tên sản phẩm:</label>
      <input type="text" id="product-name">
      <label for="product-quantity">Số lượng:</label>
      <input type="number" id="product-quantity">
      <label for="product-status">Tình trạng:</label>
      <select id="product-status">
        <option value="new">Mới</option>
        <option value="hot">Hot</option>
        <option value="sale">Sale</option>
      </select>
      <label for="product-price">Giá tiền:</label>
      <input type="number" id="product-price">
      <label for="product-category">Danh mục:</label>
      <input type="text" id="product-category">
      <button type="submit">Thêm</button>
      <button type="button" id="reset-button">Reset</button>
    </form>
    <table>
      <thead>
        <tr>
          <th>Mã sản phẩm</th>
          <th>Tên sản phẩm</th>
          <th>Số lượng</th>
          <th>Tình trạng</th>
          <th>Giá tiền</th>
          <th>Danh mục</th>
          <th>Chức năng</th>
        </tr>
      </thead>
      <tbody id="product-table-body">
      </tbody>
    </table>
  </div>
  <script src="script.js"></script>
<body>
<html>

<router-outlet></router-outlet>
