import { Component, OnInit } from '@angular/core';
import { wa } from '../watch';
import { WatchService } from '../watch.service';




@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  watches: Watch[];

  constructor(private watchService: WatchService) { }

  ngOnInit() {
    this.getWatches();
  }

  getWatches(): void {
    this.watches = this.watchService.getWatches();
  }

  addWatch(name: string, brand: string, price: number): void {
    const id = this.watches.length > 0 ? this.watches[this.watches.length - 1].id + 1 : 1;
    const watch = { id, name, brand, price };
    this.watchService.addWatch(watch);
    this.getWatches();
  }

  updateWatch(watch: Watch): void {
    this.watchService.updateWatch(watch);
    this.getWatches();
  }

  deleteWatch(id: number): void {
    this.watchService.deleteWatch(id);
    this.getWatches();
  }
}
