import { Component  } from '@angular/core';



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watch: Watch = { id: null, name: '', price: null, description: '' };
  errorMessage: string;

  constructor(private watchService: WatchService, private router: Router) {}

  registerWatch() {
    this.errorMessage = '';

    if (!this.watch.name || !this.watch.price || !this.watch.description) {
      this.errorMessage = 'All fields are required.';
      return;
    }

    if (this.watch.price < 0) {
      this.errorMessage = 'Price must be greater than or equal to 0.';
      return;
    }

    this.watchService.addWatch(this.watch);
    this.router.navigate(['/watches']);
  }
}
