import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  newProduct = {
    name: '',
    quantity: 0,
    status: '',
    price: 0,
    category: ''
  };

  products = [
    { id: 1, name: 'Đồng hồ Casio', quantity: 10, status: 'Active', price: 100, category: 'Đồng hồ thể thao' },
    { id: 2, name: 'Đồng hồ Seiko', quantity: 20, status: 'Inactive', price: 200, category: 'Đồng hồ cơ' },
    { id: 3, name: 'Đồng hồ Citizen', quantity: 30, status: 'Active', price: 300, category: 'Đồng hồ thông minh' }
  ];

  addProduct() {
    const id = this.products.length + 1;
    const product = { id, ...this.newProduct };
    this.products.push(product);
    this.newProduct = { name: '', quantity: 0, status: '', price: 0, category: '' };
  }

  deleteProduct(productId: number) {
    this.products = this.products.filter(product => product.id !== productId);
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }







      h2 {
        font-size: 24px;
        font-weight: bold;
        margin-bottom: 16px;
      }

      form div {
        margin-bottom: 8px;
      }

      label {
        display: inline-block;
        width: 100px;
        font-weight: bold;
      }

      input[type="number"],
      select,
      input[type="text"] {
        width: 200px;
        padding: 4px;
        border: 1px solid #ccc;
      }

      button {
        margin-top: 8px;
        padding: 8px;
        background-color: #007bff;
        color: #fff;
        border: none;
        border-radius: 4px;
        cursor: pointer;
      }

      button:hover {
        background-color: #0069d9;
      }

      li {
        margin-bottom: 8px;
      }


  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <h2>Quản lý sản phẩm đồng hồ</h2>

<form>
  <div>
    <label for="name">Tên sản phẩm:</label>
    <input id="name" name="name" [(ngModel)]="newProduct.name" required>
  </div>
  <div>
    <label for="quantity">Số lượng:</label>
    <input id="quantity" name="quantity" type="number" [(ngModel)]="newProduct.quantity" required>
  </div>
  <div>
    <label for="status">Trạng thái:</label>
    <select id="status" name="status" [(ngModel)]="newProduct.status" required>
      <option value="Active">Active</option>
      <option value="Inactive">Inactive</option>
    </select>
  </div>
  <div>
    <label for="price">Giá:</label>
    <input id="price" name="price" type="number" [(ngModel)]="newProduct.price" required>
  </div>
  <div>
    <label for="category">Thể loại:</label>
    <input id="category" name="category" [(ngModel)]="newProduct.category" required>
  </div>
  <button (click)="addProduct()">Thêm sản phẩm</button>
</form>

<ul>
  <li *ngFor="let product of products">
    {{ product.name }} - {{ product.price }} - {{ product.category }}
    <button (click)="deleteProduct(product.id)">Xóa</button>
  </li>
</ul>
<body>
<html>
