import { Component } from '@angular/core';
export interface Product {
  id: number;
  name: string;
  price: number;
}
@Component({
  selector: 'app-themsua',
  templateUrl: './themsua.component.html',
  styleUrls: ['./themsua.component.css']
})
export class ThemsuaComponent {
  products: Product[] = [];
  selectedProduct: Product | undefined;
  isAddingNewProduct: boolean = false;
  isEditingProduct: boolean = false;
  newProduct: Product = { id: 0, name: '', price: 0 };
  editedProduct: Product = { id: 0, name: '', price: 0 };

  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.products = this.productService.getProducts();
  }

  selectProduct(product: Product): void {
    this.selectedProduct = product;
    this.isAddingNewProduct = false;
    this.isEditingProduct = false;
    this.newProduct = { id: 0, name: '', price: 0 };
    this.editedProduct = { ...product };
  }

  addNewProduct(): void {
    this.isAddingNewProduct = true;
    this.isEditingProduct = false;
    this.selectedProduct = undefined;
    this.newProduct = { id: 0, name: '', price: 0 };
    this.editedProduct = { id: 0, name: '', price: 0 };
  }

  saveNewProduct(): void {
    this.productService.addProduct(this.newProduct);
    this.products = this.productService.getProducts();
    this.isAddingNewProduct = false;
  }

  cancelNewProduct(): void {
    this.isAddingNewProduct = false;
  }

  editProduct(): void {
    this.isAddingNewProduct = false;
    this.isEditingProduct = true;
  }

  saveEditedProduct(): void {
    this.productService.updateProduct(this.editedProduct);
    this.products = this.productService.getProducts();
    this.isEditingProduct = false;
  }

  cancelEditedProduct(): void {
    this.isEditingProduct = false;
    this.editedProduct = { ...this.selectedProduct };
  }

  deleteProduct(): void {
    if (this.selectedProduct) {
      this.productService.deleteProduct(this.selectedProduct.id);
      this.products = this.productService.getProducts();
      this.selectedProduct = undefined;
    }
  }
}
