import { Component} from '@angular/core';


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  products: Product[] = [];

  constructor(private cartService: CartService) { }

  ngOnInit(): void {
    // Initialize products array with some sample data
    this.products.push(new Product(1, 'Watch', 99.99, 'assets/images/watch.jpg'));
    this.products.push(new Product(2, 'Shirt', 24.99, 'assets/images/shirt.jpg'));
    this.products.push(new Product(3, 'Shoes', 59.99, 'assets/images/shoes.jpg'));
  }

  addToCart(product: Product) {
    this.cartService.addToCart(product);
    window.alert('Product added to cart!');
  }

}
