import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {

}


<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }










  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Khách Hàng</p>
	</div>
  <ul>
    <li *ngFor="let product of products" (click)="selectProduct(product)" [class.selected]="product === selectedProduct">
      {{product.name}} - {{product.price | currency}}
    </li>
  </ul>

  <div *ngIf="selectedProduct">
    <h2 *ngIf="!selectedProduct.id">Thêm sản phẩm</h2>
    <h2 *ngIf="selectedProduct.id">Sửa sản phẩm</h2>
    <form (ngSubmit)="selectedProduct.id ? editProduct(selectedProduct.id, name.value, price.value) : addProduct(name.value, price.value); clearSelection()">
      <label for="name">Tên sản phẩm:</label>
      <input type="text" id="name" name="name" [(ngModel)]="selectedProduct.name" required>

      <label for="price">Giá sản phẩm:</label>
      <input type="number" id="price" name="price" [(ngModel)]="selectedProduct.price" required>

      <button type="submit">{{selectedProduct.id ? 'Sửa' : 'Thêm'}}</button>
      <button type="button" (click)="deleteProduct(selectedProduct.id); clearSelection()">Xóa</button>
      <button type="button" (click)="clearSelection()">Hủy</button>
    </form>
  </div>
<body>
<html>
