import { Component } from '@angular/core';

export interface WatchProduct {
  id: number;
  name: string;
  price: number;
  description: string;
  imageUrl: string;
  brand: string;
  style: string;
  movement: string;
}
@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products: WatchProduct[] = [
    {
      id: 1,
      name: 'Swatch Originals',
      price: 50,
      description: 'The Originals Gent collection offers a wide range of colorful watches...',
      imageUrl: 'https://example.com/swatch.jpg',
      brand: 'Swatch',
      style: 'Casual',
      movement: 'Quartz',
    },
    {
      id: 2,
      name: 'Casio G-Shock',
      price: 100,
      description: 'The ultimate tough watch, this Casio G-Shock features shock resistance...',
      imageUrl: 'https://example.com/casio.jpg',
      brand: 'Casio',
      style: 'Sport',
      movement: 'Quartz',
    },
  ];

  deleteProduct(product: WatchProduct) {
    this.products = this.products.filter(p => p !== product);
  }
}
