import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {



}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }






      h1 {
        text-align: center;
      }

      table {
        margin: auto;
        width: 50%;
        border-collapse: collapse;
      }

      th, td {
        border: 1px solid black;
        padding: 5px;
      }

      th {
        background-color: #ccc;
      }

      td:first-child {
        text-align: center;
      }

      td:last-child {
        text-align: center;
      }

      button {
        background-color: #4CAF50; /* Màu xanh lá cây */
        color: white;
        padding: 5px 10px;
        border: none;
        border-radius: 3px;
        cursor: pointer;
      }

      button:hover {
        background-color: #3e8e41; /* Màu xanh lá cây đậm */
      }



  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>




  <h1>Danh sách sản phẩm</h1>
<table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Tên sản phẩm</th>
      <th>Giá</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let product of products">
      <td>{{ product.id }}</td>
      <td>{{ product.name }}</td>
      <td>{{ product.price }}</td>
      <td><button (click)="onProductSelected(product)">Thêm mới</button></td>
    </tr>
  </tbody>
</table>
<body>
<html>
