import { Component } from '@angular/core';
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'; // import FormsModule

import { AppComponent } from './app.component';
import { ThemsuaComponent } from './components/themsua/themsua.component';
interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
}
@Component({
  selector: 'app-themsua',
  templateUrl: './themsua.component.html',
  styleUrls: ['./themsua.component.css']
})
export class ThemsuaComponent {
  products: Product[] = [
    {
      id: 1,
      name: 'Đồng hồ 1',
      price: 100,
      imageUrl: 'https://via.placeholder.com/150'
    },
    {
      id: 2,
      name: 'Đồng hồ 2',
      price: 200,
      imageUrl: 'https://via.placeholder.com/150'
    },
    {
      id: 3,
      name: 'Đồng hồ 3',
      price: 300,
      imageUrl: 'https://via.placeholder.com/150'
    }
  ];

  newProduct: Product = {
    id: 0,
    name: '',
    price: 0,
    imageUrl: ''
  };

  selectedProduct: Product | null = null;

  addProduct() {
    const newId = this.products.length > 0 ? this.products[this.products.length - 1].id + 1 : 1;
    this.newProduct.id = newId;
    this.products.push(this.newProduct);
    this.newProduct = {
      id: 0,
      name: '',
      price: 0,
      imageUrl: ''
    };
  }

  editProduct(product: Product) {
    this.selectedProduct = { ...product };
  }

  saveProduct() {
    const foundIndex = this.products.findIndex(product => product.id === this.selectedProduct?.id);
    if (foundIndex !== -1) {
      this.products[foundIndex] = { ...this.selectedProduct! };
      this.selectedProduct = null;
    }
  }

  cancelEdit() {
    this.selectedProduct = null;
  }
}
