import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {


}



<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }













      .product-form {
        margin: 20px auto;
        width: 50%;
      }

      h2 {
        margin-bottom: 10px;
      }

      label {
        display: block;
        font-weight: bold;
      }

      input, textarea {
        width: 100%;
        padding: 5px;
        margin-bottom: 10px;
        border: 1px solid #ccc;
        border-radius: 3px;
        box-sizing: border-box;
      }

      button {
        background-color: #4CAF50; /* Màu xanh lá */
        color: white;
        padding: 5px 10px;
        border: none;
        border-radius: 3px;
        cursor: pointer;
      }

      button:hover {
        background-color: #3e8e41; /* Màu xanh lá đậm hơn khi rê chuột vào */
      }





  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>


<body>
<html>
