import { Component } from '@angular/core';
interface Product {
  name: string;
  description: string;
  price: number;
}

@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products: Product[] = [{
    name: "Sản phẩm 1",
    description: "Mô tả sản phẩm 1",
    price: 1000000
  }, {
    name: "Sản phẩm 2",
    description: "Mô tả sản phẩm 2",
    price: 2000000
  }];

  newProduct: Product = {
    name: "",
    description: "",
    price: 0
  };

  addProduct(productForm: any) {
    if (productForm.valid) {
      this.products.push(this.newProduct);
      this.newProduct = {
        name: "",
        description: "",
        price: 0
      };
      productForm.resetForm();
    }
  }

  deleteProduct(product: Product) {
    const index = this.products.indexOf(product);
    if (index !== -1) {
      this.products.splice(index, 1);
    }
}
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới Sản Phẩm</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Sản phẩm</p>
	</div>
  <div class="container">
    <h1 class="title">Quản lý sản phẩm</h1>
    <form #productForm="ngForm" (ngSubmit)="addProduct(productForm)">
      <div class="form-group">
        <label for="name">Tên sản phẩm:</label>
        <input type="text" id="name" name="name" required [(ngModel)]="newProduct.name">
      </div>
      <div class="form-group">
        <label for="description">Mô tả:</label>
        <textarea id="description" name="description" required [(ngModel)]="newProduct.description"></textarea>
      </div>
      <div class="form-group">
        <label for="price">Giá:</label>
        <input type="number" id="price" name="price" required [(ngModel)]="newProduct.price">
      </div>
      <button type="submit" class="btn">Thêm sản phẩm</button>
    </form>
    <table class="product-table">
      <thead>
        <tr>
          <th>Tên sản phẩm</th>
          <th>Mô tả</th>
          <th>Giá</th>
          <th>Hành động</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let product of products">
          <td>{{ product.name }}</td>
          <td>{{ product.description }}</td>
          <td>{{ product.price }} đồng</td>
          <td>
            <button (click)="deleteProduct(product)" class="btn">Xóa sản phẩm</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
<body>
<html>
