import { Component  } from '@angular/core';
import { Router } from '@angular/router';
import { Watch } from './watch.interface';
import { WatchService } from './watch.service';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watches: Watch[] = [];
  watch: Watch = {
    id: 0,
    name: '',
    brand: '',
    price: 0,
    image: '',
    description: ''
  };

  constructor(private watchService: WatchService, private router: Router) {
    this.watches = this.watchService.getAllWatches();
  }

  addWatch() {
    this.watchService.addWatch(this.watch);
    this.watch = {
      id: 0,
      name: '',
      brand: '',
      price: 0,
      image: '',
      description: ''
    };
  }

  editWatch(id: number) {
    this.router.navigate(['/watch-edit', id]);
  }

  deleteWatch(id: number) {
    this.watchService.deleteWatch(id);
  }
}
