import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">

	<title> </title>

    <style>
 /* Reset styles */
 * {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* Body styles */
body {
  font-family: Arial, sans-serif;
  background-color: #f8f8f8;
}

/* Header styles */
header {
  background-color: #007bff;
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px 20px;
}

.logo {
  font-size: 24px;
  font-weight: bold;
}

nav ul {
  list-style-type: none;
  display: flex;
}

nav li {
  margin-left: 20px;
}

nav a {
  display: block;
  color: #fff;
  text-decoration: none;
}

.logout a {
  background-color: #fff;
  color: #007bff;
  border-radius: 3px;
  padding: 10px 20px;
  text-decoration: none;
}

.logout a:hover {
  background-color: #eee;
}

/* Sidebar styles */
aside {
  background-color: #fff;
  position: fixed;
  left: 0;
  top: 60px;
  bottom: 0;
  width: 200px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
}

aside ul {
  list-style-type: none;
  padding: 10px;
}

aside li {
  margin-bottom: 10px;
}

aside a {
  display: block;
  color: #333;
  text-decoration: none;
}

aside a:hover {
  background-color: #eee;
}

/* Main content styles */
main {
  margin-top: 60px;
  margin-left: 200px;
  padding: 20px;
}
.button-group {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;
  }

  .button {
  display: inline-block;
  padding: 10px;
  font-size: 16px;
  background-color: #008CBA;
  color: white;
  border: none;
  text-decoration: none;
  margin-right: 10px;
  cursor: pointer;
  }

  .button:hover {
  background-color: #006B8F;
  }
  .search-container {
  display: flex;
  justify-content: flex-end;
  margin-top: 20px;
  }

  input[type=text] {
  width: 250px;
  padding: 10px;
  font-size: 16px;
  border: none;
  border-bottom: 2px solid #ccc;
  }

  button[type=submit] {
  padding: 10px;
  font-size: 16px;
  background-color: #008CBA;
  color: white;
  border: none;
  cursor: pointer;
  }

  button[type=submit]:hover {
  background-color: #006B8F;
  }
  table {
  border-collapse: collapse;
  width: 100%;
  max-width: 800px;
  margin:  auto;
  }

  th, td {
  border: 1px solid #ddd;
  text-align: center;
  padding: 8px;

  }


  th {
  background-color: #008CBA;
  color: white;
  }

  tr:nth-child(even) {
  background-color: #f2f2f2;
  }

  a {
  text-decoration: none;
  color: #333;
  margin-right: 10px;
  }

  a:hover {
  color: #008CBA;
  }

  .fa {
  font-size: 18px;
  cursor: pointer;
  }

  .fa-edit {
  color: #008CBA;
  }

  .fa-trash {
  color: #f44336;
  }
  .center {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  background-color: #eee;
}

.center p {
  text-align: center;
  font-size: 24px;
  color: #333;
}

/* Footer styles */
footer {
  background-color: #ccc;
  color: #333;
  text-align: center;
  padding: 10px;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
}


        </style>



</head>
<body>

 <!-- Header -->
 <header>
  <a href="#">Logout</a>

<div class="logo">Admin Panel</div>
<nav>
<ul>
<li><a href="#">Dashboard</a></li>
<li><a href="#">Products</a></li>
<li><a href="#">Orders</a></li>
<li><a href="#">Customers</a></li>
<li><a href="#">Settings</a></li>
</ul>
</nav>
<div class="logout">
<a routerLink="/hinhanh">Đăng Xuất</a>
</div>
</header>

<!-- Sidebar -->
<aside>
<ul>
<li><a routerLink="/main"> Quản Lý Sản Phẩm</a></li>
<li><a routerLink="/khachhang">Quản lý Khách Hàng</a></li>
<li><a routerLink="/donhang">Quản Lý Đơn Hàng</a></li>
<li><a routerLink="/notfound">Quản Lý Nhân Viên</a></li>
<li><a routerLink="/taikhoan">Quản Lý Tài Khoản</a></li>
</ul>
</aside>

<!-- Content -->
<main>
<!-- Content goes here -->
</main>
<div class="button-group">
  <a routerLink="/home" class="button">Tạo mới sản phẩm</a>

  <a routerLink="/khachhang" class="button">Thêm nhà cung cấp</a>


  <a routerLink="/danhmuc" class="button">Thêm danh mục sản phẩm</a>
  </div>
  <div class="search-container">
  <form action="/search">
    <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
    <button type="submit"><i class="fa fa-search"></i> Tìm kiếm</button>
  </form>
  </div>
  <div class="center">
  <p>Danh Sách Quản Lý Sản Phẩm</p>
</div>

  <table>
  <thead>
    <tr>
    <th>Mã sản phẩm</th>
    <th>Tên sản phẩm đồng hồ</th>
    <th>Số lượng</th>
    <th>Tình trạng</th>
    <th>Giá tiền</th>
    <th>Danh mục</th>
    <th>Chức năng</th>
    </tr>
  </thead>
  <tbody>
    <tr>
    <td>SP001</td>
    <td>Đồng hồ Casio</td>
    <td>50</td>
    <td>Còn hàng</td>
    <td>500.000đ</td>
    <td>Đồng hồ nam</td>
    <td>
      <a href="/home"><i class="fa fa-edit">Thêm</i></a>
      <a href="/home"><i class="fa fa-trash">Xóa</i></a>
    </td>
    </tr>
    <tr>
    <td>SP002</td>
    <td>Đồng hồ Seiko</td>
    <td>30</td>
    <td>Hết hàng</td>
    <td>1.000.000đ</td>
    <td>Đồng hồ nữ</td>
    <td>
      <a routerLink="/home"><i class="fa fa-edit">Thêm</i></a>
      <a routerLink="/home"><i class="fa fa-trash">xóa</i></a>
    </td>
    </tr>
  </tbody>

  </table>

<!-- Footer -->
<footer>
<p>&copy; 2023 My Company. All rights reserved.</p>
</footer>
<body>
<html>
  <router-outlet></router-outlet>
