import { Component } from '@angular/core';

export interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
}
@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products: Product[] = [
    { id: 1, name: 'Watch 1', price: 100, description: 'Description 1', image: 'https://bdtwatch.com/images/revue-thommen/dong-Ho-Lan-Revue-Thommen-Diver-17571-2135-175712135-result--70070001177164.jpg' },
    { id: 2, name: 'Watch 2', price: 200, description: 'Description 2', image: 'image2.jpg' },
    { id: 3, name: 'Watch 3', price: 300, description: 'Description 3', image: 'image3.jpg' },
  ];

  selectedProduct: Product | undefined;
  isEditing: boolean = false;
  showForm: boolean = false;

  editProduct(product: Product) {
    this.selectedProduct = product;
    this.isEditing = true;
    this.showForm = false;
  }

  deleteProduct(product: Product) {
    const index = this.products.indexOf(product);
    if (index >= 0) {
      this.products.splice(index, 1);
    }
  }

  addProduct() {
    const newId = this.products.length > 0 ? this.products[this.products.length - 1].id + 1 : 1;
    this.selectedProduct = {
      id: newId,
      name: 'pham phuoc vinh',
      price: 0,
      description: '',
      image: 'https://bdtwatch.com/images/revue-thommen/dong-Ho-Lan-Revue-Thommen-Diver-17571-2135-175712135-1-result--70070001177684.jpg'
    };
    this.isEditing = false;
    this.showForm = true;
  }

  saveProduct() {
    this.products.push(this.selectedProduct!);
    this.selectedProduct = undefined;
    this.showForm = false;
  }

  updateProduct() {
    // Find the index of the selected product
    const index = this.products.findIndex(product => product.id === this.selectedProduct!.id);
    if (index >= 0) {
      // Update the product
      this.products[index] = this.selectedProduct!;
      this.selectedProduct = undefined;
      this.isEditing = false;
    }
  }

  onFileSelected(event: any) {
    const file: File = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      this.selectedProduct!.image = reader.result as string;
    };
  }

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới Sản Phẩm</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Price</th>
        <th>Description</th>
        <th>Image</th>
        <th>Action</th>
      </tr>
    </thead>
    <!-- Display the product list -->
<div *ngFor="let product of products">
  <h3>{{product.name}}</h3>
  <p>{{product.description}}</p>
  <p>{{product.price}}</p>
  <img [src]="product.image" alt="{{product.name}}">
  <button (click)="editProduct(product)">Edit</button>
  <button (click)="deleteProduct(product)">Delete</button>
</div>

<!-- Form to add a new product -->
<div *ngIf="showForm">
  <h3>Add Product</h3>
  <form>
    <label for="name">Name:</label>
    <input type="text" id="name" name="name" [(ngModel)]="selectedProduct.name">
    <label for="description">Description:</label>
    <textarea id="description" name="description" [(ngModel)]="selectedProduct.description"></textarea>
    <label for="price">Price:</label>
    <input type="number" id="price" name="price" [(ngModel)]="selectedProduct.price">
    <label for="image">Image:</label>
    <input type="file" id="image" name="image" (change)="onFileSelected($event)">
    <div>
      <img [src]="selectedProduct.image" alt="{{selectedProduct.name}}">
    </div>
    <button type="button" (click)="saveProduct()">Save</button>
    <button type="button" (click)="showForm = false">Cancel</button>
  </form>
</div>

<!-- Button to show the add product form -->
<button (click)="addProduct()">Add Product</button>
  </table>
