import { Component } from '@angular/core';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent {
  productList = [
    { id: 1, name: 'Đồng hồ Rolex', price: 1000 },
    { id: 2, name: 'Đồng hồ Omega', price: 800 },
    { id: 3, name: 'Đồng hồ Casio', price: 100 }
  ];
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
	<style type="text/css">
		.button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
		  .search-container {
			display: flex;
			justify-content: flex-end;
			margin-top: 20px;
		  }

		  input[type=text] {
			width: 250px;
			padding: 10px;
			font-size: 16px;
			border: none;
			border-bottom: 2px solid #ccc;
		  }



	  </style>
</head>
<body>
	<div class="button-group">
		<a routerLink="/home" class="button">Tạo mới sản phẩm</a>

		<a routerLink="/list" class="button">Thêm Danh sách sản phẩm</a>

		<a routerLink="/danhmuc" class="button">Thêm danh mục sản phẩm</a>
	  </div>
	  <div class="search-container">
		<form action="/search">
		  <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
		  <button type="submit"><i class="fa fa-search"></i> Tìm kiếm</button>
		</form>
	  </div>
	  <div class="center">
		<p>Danh Sách Quản Lý Sản Phẩm</p>
	</div>



<body>
<html>



<router-outlet></router-outlet>
