import { Component } from '@angular/core';


interface Product {
  name: string;
  price: number;
  description: string;
  image: string;
}


@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {

  currentProduct: Product = {
    name: '',
    price: 0,
    description: '',
    image: ''
  };

  saveProduct() {
    // TODO: Add code to save product
  }

  cancelProduct() {
    // TODO: Add code to cancel adding product
  }

  handleImageChange(event: any) {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      this.currentProduct.image = reader.result as string;
    };
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <h1>Add Product</h1>

  <div id="product-form">
    <form>
      <label for="name">Name:</label>
      <input type="text" id="name" [(ngModel)]="currentProduct.name">
      <br>
      <label for="price">Price:</label>
      <input type="number" id="price" [(ngModel)]="currentProduct.price">
      <br>
      <label for="description">Description:</label>
      <textarea id="description" [(ngModel)]="currentProduct.description"></textarea>
      <br>
      <label for="image">Image:</label>
      <input type="file" id="image" (change)="handleImageChange($event)">
      <br>
      <button type="button" (click)="saveProduct()">Save</button>
      <button type="button" (click)="cancelProduct()">Cancel</button>
    </form>
  </div>
<body>
<html>
