import { Component} from '@angular/core';
interface Product {
  name: string;
  description: string;
  price: number;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  products: Product[] = [{
    name: "Sản phẩm 1",
    description: "Mô tả sản phẩm 1",
    price: 1000000
  }, {
    name: "Sản phẩm 2",
    description: "Mô tả sản phẩm 2",
    price: 2000000
  }];

  newProduct: Product = {
    name: "",
    description: "",
    price: 0
  };

  addProduct() {
    this.products.push(this.newProduct);
    this.newProduct = {
      name: "",
      description: "",
      price: 0
    };
  }

  deleteProduct(product: Product) {
    const index = this.products.indexOf(product);
    if (index !== -1) {
      this.products.splice(index, 1);
    }
}
}










<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>


</head>
<div class="container">
	<h1 class="title">Quản lý sản phẩm</h1>
	<div class="product-form">
	  <form>
		<div class="form-group">
		  <label for="name">Tên sản phẩm:</label>
		  <input type="text" id="name" [(ngModel)]="newProduct.name" name="name">
		</div>
		<div class="form-group">
		  <label for="description">Mô tả:</label>
		  <textarea id="description" [(ngModel)]="newProduct.description" name="description"></textarea>
		</div>
		<div class="form-group">
		  <label for="price">Giá:</label>
\
		</div>
		<button type="submit" (click)="addProduct()" class="btn">Thêm sản phẩm</button>
	  </form>
	</div>

	<div class="product-list">
	  <div *ngFor="let product of products" class="product">
		<h2>{{ product.name }}</h2>
		<p>{{ product.description }}</p>
		<p>{{ product.price }} đồng</p>
		<button (click)="deleteProduct(product)" class="btn">Xóa sản phẩm</button>
	  </div>
	</div>
  </div>
<body>
<html>
