import { Component } from '@angular/core';



export interface Product {
  name: string;
  price: number;
  description: string;
}

const PRODUCTS: Product[] = [
  { name: 'Đồng hồ Rolex', price: 5000, description: 'Đồng hồ cao cấp' },
  { name: 'Đồng hồ Casio', price: 500, description: 'Đồng hồ thời trang' },
  { name: 'Đồng hồ Seiko', price: 1000, description: 'Đồng hồ chính hãng' },
];

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  displayedColumns: string[] = ['name', 'price', 'description', 'actions'];
  dataSource = new MatTableDataSource(PRODUCTS);

  constructor(public dialog: MatDialog) {}

  openDialog(product?: Product): void {
    const dialogRef = this.dialog.open(ProductDialogComponent, {
      width: '250px',
      data: product ? { ...product } : {}
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        if (product) {
          Object.assign(product, result);
          this.dataSource.data = [...this.dataSource.data];
        } else {
          this.dataSource.data.push(result);
          this.dataSource.data = [...this.dataSource.data];
        }
      }
    });
  }

  deleteProduct(product: Product): void {
    const index = this.dataSource.data.indexOf(product);
    if (index >= 0) {
      this.dataSource.data.splice(index, 1);
      this.dataSource.data = [...this.dataSource.data];
    }
  }
}
}
