import { Component } from '@angular/core';



@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  products = [
    {
      brand: 'Rolex',
      model: 'Submariner',
      price: 5000000,
      imageUrl: 'https://images.pexels.com/photos/125779/pexels-photo-125779.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1/150x150'
    },
    {
      brand: 'Omega',
      model: 'Seamaster',
      price: 7000000,
      imageUrl: 'https://images.pexels.com/photos/364822/rolex-watch-time-luxury-364822.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1/150x150'
    },
    {
      brand: 'Seiko',
      model: 'Presage',
      price: 2000000,
      imageUrl: 'https://images.pexels.com/photos/280250/pexels-photo-280250.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1/150x150'
    },
    {
      brand: 'Seiko',
      model: 'Presage',
      price: 2000000,
      imageUrl: 'https://images.pexels.com/photos/277319/pexels-photo-277319.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
      brand: 'Seiko',
      model: 'Presage',
      price: 2000000,
      imageUrl: 'https://images.pexels.com/photos/23475/pexels-photo.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },

  ];
}











<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
<script>
  class Watch {
    constructor(brand, model, price, imageUrl) {
      this.brand = brand;
      this.model = model;
      this.price = price;
      this.imageUrl = imageUrl;
    }
  }

  class WatchFormComponent {
    constructor() {
      this.form = document.querySelector('form');
      this.form.addEventListener('submit', this.onSubmit.bind(this));
    }

    onSubmit(event) {
      event.preventDefault();

      const brand = this.form.brand.value;
      const model = this.form.model.value;
      const price = parseFloat(this.form.price.value);
      const imageUrl = this.form['image-url'].value;

      const newWatch = new Watch(brand, model, price, imageUrl);

      this.form.reset();

      // emit new watch to parent component
    }
  }


</script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }








      .product {
        display: flex;
        align-items: center;
        margin-bottom: 1rem;
        margin: auto;
        width: 70%;

      }

      .product-image {
        width: 250px;
        height: 250px;
        object-fit: contain;
        margin-right: 1rem;
      }

      .product-details {
        display: flex;
        flex-direction: column;
      }

      .product-brand {
        font-weight: bold;
        margin-bottom: 0.5rem;
      }

      .product-model {
        margin-bottom: 0.5rem;
      }

      .product-price {
        font-weight: bold;
      }


  </style>
</head>
<div class="button-group">
  <a routerLink="/home" class="button">Tạo mới sản phẩm</a>

  <a routerLink="/list" class="button">Thêm Danh sách sản phẩm</a>

  <a routerLink="/danhmuc" class="button">Thêm danh mục sản phẩm</a>
  </div>
  <div class="search-container">
  <form action="/search">
    <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
    <button type="submit"><i class="fa fa-search"></i> Tìm kiếm</button>
  </form>
  </div>
<h2> Danh Sách Sản Phẩm</h2>
<div class="product" *ngFor="let product of products">
  <img class="product-image" src="{{ product.imageUrl }}" alt="{{ product.model }}">
  <div class="product-details">
    <div class="product-brand">{{ product.brand }}</div>
    <div class="product-model">{{ product.model }}</div>
    <div class="product-price">{{ product.price | currency }}</div>
  </div>
</div>
<body>
<html>
