import { Component } from '@angular/core';

import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'; // import FormsModule here



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  newProduct = {
    name: '',
    description: '',
    price: ''
  };
  editingProduct: any = null;
  nextId = 4;

  products = [
    { id: 1, name: 'Rolex', description: 'Luxury watch', price: '5000' },
    { id: 2, name: 'Casio', description: 'Digital watch', price: '100' },
    { id: 3, name: 'Timex', description: 'Classic watch', price: '50' }
  ];

  addProduct() {
    this.products.push({
      id: this.nextId,
      name: this.newProduct.name,
      description: this.newProduct.description,
      price: this.newProduct.price
    });

    // Reset the newProduct object
    this.newProduct = {
      name: '',
      description: '',
      price: ''
    };

    this.nextId++;
  }

  deleteProduct(productId: number) {
    // Find the index of the product with the given id
    const index = this.products.findIndex(product => product.id === productId);

    // If the product exists, remove it from the array
    if (index !== -1) {
      this.products.splice(index, 1);
    }
  }

  editProduct(product: any) {
    // Set the editingProduct object to a copy of the selected product
    this.editingProduct = { ...product };
  }

  updateProduct() {
    // Find the index of the editingProduct in the`products` array
    const index = this.products.findIndex(product => product.id === this.editingProduct.id);

    // If the editingProduct exists, update it in the array
    if (index !== -1) {
      this.products[index] = { ...this.editingProduct };
    }

    // Reset the editingProduct object
    this.editingProduct = null;
  }

  cancelEdit() {
    // Reset the editingProduct object
    this.editingProduct = null;
  }
}
