import { Component } from '@angular/core';


@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  watches: Watch[] = [
    { id: 1, name: 'Watch 1', brand: 'Brand 1', price: 100, description: 'Description 1' },
    { id: 2, name: 'Watch 2', brand: 'Brand 2', price: 200, description: 'Description 2' },
    { id: 3, name: 'Watch 3', brand: 'Brand 3', price: 300, description: 'Description 3' },
  ];

  selectedWatch: Watch = { id: 0, name: '', brand: '', price: 0, description: '' };
  isEditing: boolean = false;
  showForm: boolean = false;

  addWatch() {
    this.selectedWatch = { id: 0, name: '', brand: '', price: 0, description: '' };
    this.isEditing = false;
    this.showForm = true;
  }

  editWatch(watch: Watch) {
    this.selectedWatch = { ...watch };
    this.isEditing = true;
    this.showForm = true;
  }

  deleteWatch(watch: Watch) {
    const index = this.watches.indexOf(watch);
    if (index >= 0) {
      this.watches.splice(index, 1);
    }
  }

  onSubmit() {
    if (this.isEditing) {
      const index = this.watches.findIndex(watch => watch.id === this.selectedWatch.id);
      if (index >= 0) {
        this.watches[index] = this.selectedWatch;
      }
    } else {
      const newId = this.watches.length > 0 ? this.watches[this.watches.length - 1].id + 1 : 1;
      this.selectedWatch.id = newId;
      this.watches.push(this.selectedWatch);
    }
    this.showForm = false;
  }
}
