import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  private apiUrl = 'api/watch-types';

  constructor(private http: HttpClient) { }

  getWatchTypes(): Observable<WatchType[]> {
    return this.http.get<WatchType[]>(this.apiUrl);
  }

  addWatchType(watchType: WatchType): Observable<WatchType> {
    return this.http.post<WatchType>(this.apiUrl, watchType);
  }
}
