import { Component } from '@angular/core';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
    <script>
        const productForm = document.getElementById('product-form');
        const productIdInput = document.getElementById('product-id');
        const productNameInput = document.getElementById('product-name');
        const productImageInput = document.getElementById('product-image');
        const productPriceInput = document.getElementById('product-price');
        const productDescriptionInput = document.getElementById('product-description');
        const saveButton = document.getElementById('save-button');
        const cancelButton = document.getElementById('cancel-button');
        const productTableBody = document.getElementById('product-table-body');
        const printButton = document.getElementById('print-button');

        let products = [];

        // Tạo HTML cho một sản phẩm trong bảng
        function createProductTableRow(product) {
            const productTableRow = `
                <tr>
                    <td>${product.id}</td>
                    <td>${product.name}</td>
                    <td><img src="${product.image}" alt="${product.name}" width="100"></td>
                    <td>${product.price}đ</td>
                    <td>${product.description}</td>
                    <td>
                        <button class="edit-button" data-product-id="${product.id}">Sửa</button>
                        <button class="delete-button" data-product-id="${product.id}">Xóa</button>
                    </td>
                </tr>
            `;
            return productTableRow;
        }

        // Hiển thị danh sách sản phẩm trong bảng
        function displayProductList() {
            productTableBody.innerHTML = '';
            for (const product of products) {
                const productTableRow = createProductTableRow(product);
                productTableBody.insertAdjacentHTML('beforeend', productTableRow);
            }
        }

        // Lấy ID mới cho sản phẩm
        function getNewProductId() {
            let newId = 1;
            if (products.length > 0) {
                const lastProduct = products[products.length - 1];
                newId = lastProduct.id + 1;
            }
            return newId;
        }

        // Xử lý khi bấm nút Lưu trong form
        function handleSaveButtonClick(event) {
            event.preventDefault();
            if (productNameInput.value.trim() === '' || productImageInput.value.trim() === '' || productPriceInput.value.trim() === '' || productDescriptionInput.value.trim() === '') {
                alert('Vui lòng nhập đầy đủ thông tin sản phẩm!');
                return;
            }
            const productId = productIdInput.value ? parseInt(productIdInput.value) : null;
            const productName = productNameInput.value;
            const productImage = productImageInput.value;
            const productPrice = parseInt(productPriceInput.value);
            const productDescription = productDescriptionInput.value;
            if (productId) {
                // Sửa sản phẩm
                const productIndex = products.findIndex(product => product.id === productId);
                if (productIndex >= 0) {
                    const product = products[productIndex];
                    product.name = productName;
                    product.image = productImage;
                    product.price = productPrice;
                    product.description = productDescription;
                    displayProductList();
                    resetProductForm();
                    alert('Sản phẩm đã được cập nhật thành công!');
                } else {
                    alert('Không tìm thấy sản phẩm để cập nhật!');
                }
            } else {
                // Thêm sản phẩm mới
                const newProductId = getNewProductId();
                const newProduct = {
                    id: newProductId,
                    name: productName,
                    image: productImage,
                    price: productPrice,
                    description: productDescription,
                };
                products.push(newProduct);
                const productTableRow = createProductTableRow(newProduct);
                productTableBody.insertAdjacentHTML('beforeend', productTableRow);
                resetProductForm();
                alert('Sản phẩm đã được thêm mới thành công!');
            }
        }

        // Xử lý khi bấm nút Hủy trong form
        function handleCancelButtonClick(event) {
            event.preventDefault();
            resetProductForm();
        }

        // Xử lý khi bấm nút Sửa hoặc Xóa trong bảng
        function handleEditOrDeleteButtonClick(event) {
            const target = event.target;
            const productId = parseInt(target.getAttribute('data-product-id'));
            if (target.classList.contains('edit-button')) {
                // Hiển thị thông tin sản phẩm trong form để sửa
                const productIndex = products.findIndex(product => product.id === productId);
                if (productIndex >= 0) {
                    const product = products[productIndex];
                    productIdInput.value = product.id;
                    productNameInput.value = product.name;
                    productImageInput.value = product.image;
                    productPriceInput.value = product.price;
                    productDescriptionInput.value = product.description;
                    saveButton.textContent = 'Cập nhật';
                } else {
                    alert('Không tìm thấy sản phẩm để sửa!');
                }
            } else if (target.classList.contains('delete-button')) {
                // Xóa sản phẩm
                const confirmDelete = confirm('Bạn có chắc muốn xóa sản phẩm này?');
                if (confirmDelete) {
                    const productIndex = products.findIndex(product => product.id === productId);
                    if (productIndex >= 0) {
                        products.splice(productIndex, 1);
                        displayProductList();
                        resetProductForm();
                        alert('Sản phẩm đã được xóa thành công!');
                    } else {
                        alert('Không tìm thấy sản phẩm để xóa!');
                    }
                }
            }
        }

        // Reset form thêm/sửa sản phẩm
        function resetProductForm() {
            productForm.reset();
            productIdInput.value = '';
            saveButton.textContent = 'Lưu';
        }

        // Xử lý khi bấm nút In danh sách sản phẩm
        function handlePrintButtonClick(event) {
            event.preventDefault();
            window.print();
        }

        // Gán


    </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
      .container {
        max-width: 800px;
        margin: 0 auto;
        text-align: center;
    }

    h1 {
        font-size: 32px;
        margin-bottom: 40px;
    }

    .form-container {
        margin-bottom: 40px;
    }

    .form-group {
        margin-bottom: 20px;
        text-align: left;
    }

    label {
        display: block;
        margin-bottom: 5px;
    }

    input[type="text"],
    input[type="number"],
    textarea {
        width: 100%;
        padding: 10px;
        border: 1px solid #ccc;
        border-radius: 3px;
    }

    button {
        padding: 10px 20px;
        background-color: #4CAF50;
        color: #fff;
        border: none;
        border-radius: 3px;
        cursor: pointer;
    }

    button:hover {
        background-color: #3e8e41;
    }

    table {
        border-collapse: collapse;
        width: 100%;
        margin-bottom: 20px;
    }

    th, td {
        padding: 8px;
        text-align: left;
        border-bottom: 1px solid #ddd;
    }

    th {
        background-color: #4CAF50;
        color: white;
    }

    #print-button {
        margin-top: 20px;
    }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Khách Hàng</p>
	</div>
    <div id="product-list">
        <table>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Tên sản phẩm</th>
                    <th>Ảnh sản phẩm</th>
                    <th>Giá sản phẩm</th>
                    <th>Mô tả sản phẩm</th>
                    <th></th>
                </tr>
            </thead>
            <tbody id="product-table-body">
                <!-- Danh sách sản phẩm sẽ được tạo ở đây -->
            </tbody>
        </table>
        <button id="print-button">In danh sách sản phẩm</button>
    </div>

<script src="script.js"></script>
