import { Component } from '@angular/core';



@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  watches: Watch[] = [
    new Watch('Rolex', 'Daytona', 5000, 'https://example.com/rolex-daytona.jpg'),
    new Watch('Omega', 'Seamaster', 3000, 'https://example.com/omega-seamaster.jpg'),
    new Watch('Tag Heuer', 'Carrera', 4000, 'https://example.com/tag-heuer-carrera.jpg')
  ];

  addWatch(newWatch: Watch) {
    this.watches.push(newWatch);
  }

  deleteWatch(watch: Watch) {
    const index = this.watches.indexOf(watch);
    if (index !== -1) {
      this.watches.splice(index, 1);
    }
}

}









<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 100px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }









  </style>
</head>
<table>
  <thead>
    <tr>
      <th>Brand</th>
      <th>Model</th>
      <th>Price</th>
      <th>Image</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let watch of watches">
      <td>{{ watch.brand }}</td>
      <td>{{ watch.model }}</td>
      <td>{{ watch.price }}</td>
      <td><img [src]="watch.imageUrl" alt="{{ watch.brand }} {{ watch.model }}"></td>
      <td><button (click)="deleteWatch(watch)">Delete</button></td>
    </tr>
  </tbody>
</table>
<body>
<html>
