import { Component } from '@angular/core';
export interface Product {
  id: number;
  name: string;
  price: number;
}
@Component({
  selector: 'app-themsua',
  templateUrl: './themsua.component.html',
  styleUrls: ['./themsua.component.css']
})
export class ThemsuaComponent {
  private products: Product[] = [
    { id: 1, name: 'Watch A', price: 100 },
    { id: 2, name: 'Watch B', price: 200 },
    { id: 3, name: 'Watch C', price: 300 },
  ];

  constructor() { }

  getProducts(): Product[] {
    return this.products;
  }
}
