import { Component } from '@angular/core';


interface Product {
  id: number;
  name: string;
  price: number;
  imageUrl: string;
}
@Component({
  selector: 'app-themsua',
  templateUrl: './themsua.component.html',
  styleUrls: ['./themsua.component.css']
})
export class ThemsuaComponent {
  newProduct: Product = {
    id: 0,
    name: '',
    price: 0,
    imageUrl: ''
  };
}
