import { Component } from '@angular/core';

@Component({
  selector: 'app-tongket',
  templateUrl: './tongket.component.html',
  styleUrls: ['./tongket.component.css']
})
export class TongketComponent {

}

<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
    <script>
// lấy các phần tử HTML
const totalOrders = document.getElementById('total-orders');
const totalRevenue = document.getElementById('total-revenue');
const totalProfit = document.getElementById('total-profit');

// tạo một đối tượng giả lập dữ liệu
const data = {
  orders: 100,
  revenue: 10000,
 profit: 5000
};

// cập nhật dữ liệu trên trang
totalOrders.textContent = data.orders;
totalRevenue.textContent = `$${data.revenue}`;
totalProfit.textContent = `$${data.profit}`;


    </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
      body {
        margin: 0;
        padding: 0;
        font-family: Arial, sans-serif;
      }

      .admin-panel {
        max-width: 550px;
        margin: 0 auto;
        display: flex;
        justify-content: center;
        align-items: center;
      }

      .sidebar {
        width: 200px;
        background-color: #f2f2f2;
        color: #555;
        padding: 20px;
      }

      .sidebar ul {
        list-style: none;
        padding: 0;
        margin: 0;
      }

      .sidebar a {
        display: block;
        color: inherit;
        text-decoration: none;
        padding: 10px 0;
      }

      .sidebar a:hover {
        background-color: #ddd;
      }

      .content {
        flex: 1;
        margin-left: 20px;
      }

      .topbar {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 20px;
      }

      .search-box {
        display: flex;
        align-items: center;
      }

      .search-box input {
        padding: 10px;
        border: none;
        border-radius: 5px;
        margin-right: 10px;
      }

      .search-box button {
        padding: 10px;
        border: none;
        border-radius: 5px;
        background-color: #3498db;
        color: #fff;
        cursor: pointer;
      }

      .dashboard {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        align-items: center;
      }

      .dashboard-summary {
        margin-bottom: 40px;
      }

      .dashboard-summary h2 {
        margin-bottom: 20px;
      }

      .dashboard-summary-cards {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        align-items: center;
      }

      .dashboard-summary-card {
        width: 200px;
        height: 200px;
        margin: 20px;
        background-color: #f2f2f2;
        text-align: center;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
      }

      .dashboard-summary-card p {
        margin-top: 10px;
      }

      .dashboard-summary-card h3 {
        margin-top: 5px;
      }

  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>


