import { Component } from '@angular/core';



@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  product: Product[] = [
    { id: 1, name: 'Đồng hồ nam Casio', price: 1000000, imageUrl: 'https://example.com/product1.jpg' },
    { id: 2, name: 'Đồng hồ nữ Seiko', price: 2000000, imageUrl: 'https://example.com/product2.jpg' },
    { id: 3, name: 'Đồng hồ nam Citizen', price: 1500000, imageUrl: 'https://example.com/product3.jpg' },
    { id: 4, name: 'Đồng hồ nữ Fossil', price: 1800000, imageUrl: 'https://example.com/product4.jpg' }
  ];
}
