import { Component  } from '@angular/core';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  watchForm: FormGroup;
  watches: Watch[];

  constructor(private formBuilder: FormBuilder, private watchService: WatchService) {
    this.createForm();
    this.watches = this.watchService.getWatches();
  }

  createForm() {
    this.watchForm = this.formBuilder.group({
      name: ['', Validators.required],
      brand: ['', Validators.required],
      price: ['', Validators.required],
      image: ['', Validators.required]
    });
  }

  onSubmit() {
    const watch: Watch = {
      name: this.watchForm.value.name,
      brand: this.watchForm.value.brand,
      price: this.watchForm.value.price,
      image: this.watchForm.value.image
    };
    this.watchService.addWatch(watch);
    this.watches = this.watchService.getWatches();
    this.watchForm.reset();
  }
}
