import { Component,  OnInit } from '@angular/core';

export class Product {
  id: number;
  name: string;
  price: number;
  image: string;
  quantity: number;

  constructor(id: number, name: string, price: number, image: string, quantity: number) {
    this.id = id;
    this.name = name;
    this.price = price;
    this.image = image;
    this.quantity = quantity;
  }
}
@Component({
  selector: 'app-spham',
  templateUrl: './spham.component.html',
  styleUrls: ['./spham.component.css']
})
export class SphamComponent {
  cartItems: Product[] = [];

  constructor() { }

  ngOnInit(): void {
    // Initialize cart items array with some sample data
    this.cartItems.push(new Product(1, 'Watch', 99.99, 'assets/images/watch.jpg', 1));
    this.cartItems.push(new Product(2, 'Shirt', 24.99, 'assets/images/shirt.jpg', 2));
    this.cartItems.push(new Product(3, 'Shoes', 59.99, 'assets/images/shoes.jpg', 1));
  }

  removeFromCart(item: Product) {
    const index = this.cartItems.indexOf(item);
    if (index > -1) {
      this.cartItems.splice(index, 1);
    }
  }

  getTotal() {
    return this.cartItems.reduce((acc, item) => acc + (item.price * item.quantity), 0);
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/home" class="button">Tạo mới Sản Phẩm</a>
    <a routerLink="/list" class="button">Thêm danh mục</a>
    <a routerLink="/danhmuc" class="button">In dữ liệu</a>


  </div>
  <div class="cart-container">
    <h2></h2>
    <div *ngIf="cartItems.length === 0" class="empty-cart">
      Your cart is empty.
    </div>
    <div *ngIf="cartItems.length > 0">
      <div class="cart-item" *ngFor="let item of cartItems">
        <img [src]="item.image" alt="{{ item.name }}" class="cart-item-image">
        <div class="cart-item-details">
          <div class="cart-item-name">{{ item.name }}</div>
          <div class="cart-item-price">{{ item.price | currency }}</div>
          <div class="cart-item-quantity">Quantity: {{ item.quantity }}</div>
          <button class="cart-item-remove" (click)="removeFromCart(item)">Remove from cart</button>
        </div>
      </div>
      <div class="cart-total">
        Total: {{ getTotal() | currency }}
      </div>
    </div>
  </div>
<body>
<html>
