import { Component, OnInit } from '@angular/core';
import { Watch } from './watch';
import { WatchService } from './watch.service';




@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  watches: Watch[] = [];
  newWatch: Watch = { name: '', price: 0, brand: '' };
  showForm = false;

  constructor(private watchService: WatchService) {}

  ngOnInit() {
    this.getWatches();
  }

  getWatches() {
    this.watchService.getWatches().subscribe((watches) => {
      this.watches = watches;
    });
  }

  addWatch() {
    this.watchService.addWatch(this.newWatch).subscribe(() => {
      this.getWatches();
      this.newWatch = { name: '', price: 0, brand: '' };
      this.showForm = false;
    });
  }

  cancelAdd() {
    this.newWatch = { name: '', price: 0, brand: '' };
    this.showForm = false;
  }

  deleteWatch(watch: Watch) {
    this.watchService.deleteWatch(watch.id).subscribe(() => {
      this.getWatches();
    });
  }

  showAddForm() {
    this.showForm = true;
  }
}
