import { Component } from '@angular/core';
export interface Product {
  id: number;
  name: string;
  price: number;
}

@Component({
  selector: 'app-themsua',
  templateUrl: './themsua.component.html',
  styleUrls: ['./themsua.component.css']
})
export class ThemsuaComponent {
  @Input() product: Product;
  @Output() save = new EventEmitter<Product>();
  @Output() cancel = new EventEmitter<void>();

  saveProduct() {
    this.save.emit(this.product);
  }

  cancel() {
    this.cancel.emit();
  }
}
