import { Component } from '@angular/core';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
}

@Component({
  selector: 'app-insp',
  templateUrl: './insp.component.html',
  styleUrls: ['./insp.component.css']
})
export class InspComponent {
  products: Product[] = [
    { id: 1, name: 'Product 1', description: 'Description 1', price: 100, quantity: 10 },
    { id: 2, name: 'Product 2', description: 'Description 2', price: 200, quantity: 20 },
    { id: 3, name: 'Product 3', description: 'Description 3', price: 300, quantity: 30 },
  ];

  selectedProduct: Product = { id: 0, name: '', description: '', price: 0, quantity: 0 };
  isEditing: boolean = false;
  showForm: boolean = false;

  addProduct() {
    this.selectedProduct = { id: 0, name: '', description: '', price: 0, quantity: 0 };
    this.isEditing = false;
    this.showForm = true;
  }

  editProduct(product: Product) {
    this.selectedProduct = { ...product };
    this.isEditing = true;
    this.showForm = true;
  }

  deleteProduct(product: Product) {
    const index = this.products.indexOf(product);
    if (index >= 0) {
      this.products.splice(index, 1);
    }
  }

  onSubmit() {
    if (this.isEditing) {
      const index = this.products.findIndex(product => product.id === this.selectedProduct.id);
      if (index >= 0) {
        this.products[index] = this.selectedProduct;
      }
    } else {
      const newId = this.products.length > 0 ? this.products[this.products.length - 1].id + 1 : 1;
      this.selectedProduct.id = newId;
      this.products.push(this.selectedProduct);
    }
    this.showForm = false;
  }
}
