import { Component } from '@angular/core';
interface Product {
  name: string;
  price: number;
  description: string;
  image: string;
}


@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})

export class QlspComponent {
  products: Product[] = [
    { name: 'Product 1', price: 10, description: 'Description 1', image: 'https://www.doseco.vn/_next/image?url=https%3A%2F%2Fdoseco.vn%2Fmedia%2F2022-01%2F20190119_181120_DSC06418-.jpg&w=1200&q=75' },
    { name: 'Product 2', price: 20, description: 'Description 2', image: 'image2.jpg' },
    { name: 'Product 3', price: 30, description: 'Description 3', image: 'image3.jpg' },
  ];

  newProduct: Product = { name: '', price: 0, description: '', image: '' };

  addProduct() {
    // Tạo một ID mới cho sản phẩm
    const id = this.products.length + 1;
    // Thêm sản phẩm mới vào danh sách
    this.products.push
    // Reset form
    this.newProduct = { name: '', price: 0, description: '', image: '' };
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <table>
    <thead>
      <tr>
        <th>Name</th>
        <th>Price</th>
        <th>Description</th>
        <th>Image</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products">
        <td>{{ product.name }}</td>
        <td>{{ product.price }}</td>
        <td>{{ product.description }}</td>
        <td><img [src]="product.image" alt="{{ product.name }}"></td>
      </tr>
    </tbody>
  </table>
<body>
<html>
