import { Component, Input, Output, EventEmitter  } from '@angular/core';

@Component({
  selector: 'app-spham',
  templateUrl: './spham.component.html',
  styleUrls: ['./spham.component.css']
})
export class SphamComponent {
  @Input() product: any;
  @Output() close: EventEmitter<any> = new EventEmitter();

  close() {
    this.close.emit();
  }
}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>

</head>
<body>
  <div class="button-group">
    <a routerLink="/home" class="button">Tạo mới Sản Phẩm</a>
    <a routerLink="/list" class="button">Thêm danh mục</a>
    <a routerLink="/danhmuc" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>



      <app-product-cart *ngFor="let item of cartItems" [product]="item" (view)="viewProduct($event)"></app-product-cart>
<app-product-details *ngIf="selectedProduct" [product]="selectedProduct" (close)="closeDetails()"></app-product-details>
