import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  watches: Watch[];
  selectedWatch: Watch;
  isAdding: boolean;
  isEditing: boolean;

  constructor(private watchService: WatchService) { }

  ngOnInit() {
    this.getWatches();
  }

  getWatches(): void {
    this.watchService.getWatches()
      .subscribe(watches => this.watches = watches);
  }

  addWatch(): void {
    this.watchService.addWatch(this.selectedWatch)
      .subscribe(watch => {
        this.watches.push(watch);
        this.cancelAdd();
      });
  }

  updateWatch(): void {
    this.watchService.updateWatch(this.selectedWatch)
      .subscribe(watch => {
        const index = this.watches.findIndex(w => w.id === watch.id);
        this.watches[index] = watch;
        this.cancelEdit();
      });
  }

  deleteWatch(id: number): void {
    this.watchService.deleteWatch(id)
      .subscribe(() => {
        this.watches = this.watches.filter(w => w.id !== id);
      });
  }

  selectWatch(watch: Watch): void {
    this.selectedWatch = { id: watch.id, name: watch.name, brand: watch.brand, price: watch.price };
  }

  cancelAdd(): void {
    this.selectedWatch = null;
    this.isAdding = false;
  }

  cancelEdit(): void {
    this.selectedWatch = null;
    this.isEditing = false;
  }
}
