import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  products = [
    { id: 1, name: 'Đồng hồ Casio', price: 100 },
    { id: 2, name: 'Đồng hồ Citizen', price: 200 },
    { id: 3, name: 'Đồng hồ Seiko', price: 150 },
  ];

  selectedProduct = null;

  addProduct(name: string, price: number) {
    const id = this.products.length + 1;
    this.products.push({ id, name, price });
  }

  editProduct(id: number, name: string, price: number) {
    const index = this.products.findIndex(p => p.id === id);
    this.products[index] = { id, name, price };
  }

  deleteProduct(id: number) {
    const index = this.products.findIndex(p => p.id === id);
    this.products.splice(index, 1);
  }

  selectProduct(product) {
    this.selectedProduct = product;
  }

  clearSelection() {
    this.selectedProduct = null;
  }
}


<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }

      button[type=submit] {
        padding: 10px;
        font-size: 16px;
        background-color: #008CBA;
        color: white;
        border: none;
        cursor: pointer;
      }

      button[type=submit]:hover {
        background-color: #006B8F;
      }
      .center {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 200px;
        background-color: #eee;
      }

      .center p {
        text-align: center;
        font-size: 24px;
        color: #333;
      }
  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <div class="search-container">
    <form action="/search">
      <input type="text" placeholder="Nhập từ khóa tìm kiếm" name="search">
      <button type="submit"><i class="fa fa-search"></i>Tìm Kiếm</button>
    </form>
  </div>
  <div class="center">
		<p>Quản Lý Khách Hàng</p>
	</div>
  <ul>
    <li *ngFor="let item of items">
      {{item.name}} - {{item.price | currency}}
    </li>
  </ul>
<body>
<html>
