import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Watch } from '../watch';
import { WatchService } from '../watch.service';



@Component({
  selector: 'app-qlsp',
  templateUrl: './qlsp.component.html',
  styleUrls: ['./qlsp.component.css']
})
export class QlspComponent {
  watchForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private watchService: WatchService
  ) {
    this.watchForm = this.formBuilder.group({
      name: ['', Validators.required],
      brand: ['', Validators.required],
      price: [null, Validators.required]
    });
  }

  onSubmit() {
    const watch: Watch = {
      id: null,
      name: this.watchForm.value.name,
      brand: this.watchForm.value.brand,
      price: this.watchForm.value.price
    };
    this.watchService.addWatch(watch);
    this.watchForm.reset();
  }
}
