import { Component } from '@angular/core';

@Component({
  selector: 'app-danhmuc',
  templateUrl: './danhmuc.component.html',
  styleUrls: ['./danhmuc.component.css']
})
export class DanhmucComponent {
  products = [
    { name: 'Đồng hồ Rolex', price: 5000000, quantity: 2 },
    { name: 'Đồng hồ Omega', price: 7000000, quantity: 1 },
    { name: 'Đồng hồ Seiko', price: 2000000, quantity: 3 }
  ];

}
<!DOCTYPE html>
<html>
<head>
	<meta charset="utf-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
	<title></title>
  <script>
    const form = document.querySelector('#product-form');
    const table = document.querySelector('#product-table tbody');

    form.addEventListener('submit', (event) => {
      // Prevent default form submission
      event.preventDefault();

      // Get form values
      const name = document.querySelector('#name').value;
      const price = document.querySelector('#price').value;
      const quantity = document.querySelector('#quantity').value;
      const image = document.querySelector('#image').value;

      // Create new table row with product information
      const newRow = table.insertRow();
      const nameCell = newRow.insertCell(0);
      const priceCell = newRow.insertCell(1);
      const quantityCell = newRow.insertCell(2);
      const imageCell = newRow.insertCell(3);
      nameCell.textContent = name;
      priceCell.textContent = price;
      quantityCell.textContent = quantity;
      if (image) {
        const img = document.createElement('img');
        img.src = image;
        img.alt = name;
        img.width = 100;
        imageCell.appendChild(img);
      }

      // Reset form
      form.reset();
    });
  </script>
  <style>
    .button-group {
			display: flex;
			justify-content: center;
			align-items: center;
			margin-bottom: 20px;
		  }

		  .button {
			display: inline-block;
			padding: 10px;
			font-size: 16px;
			background-color: #008CBA;
			color: white;
			border: none;
			text-decoration: none;
			margin-right: 10px;
			cursor: pointer;
		  }

		  .button:hover {
			background-color: #006B8F;
		  }
      .search-container {
        display: flex;
        justify-content: flex-end;
        margin-top: 20px;
      }

      input[type=text] {
        width: 250px;
        padding: 10px;
        font-size: 16px;
        border: none;
        border-bottom: 2px solid #ccc;
      }














      table {
        width: 80%;
        border-collapse: collapse;
        margin-bottom: 1rem;
      }

      th,
      td {
        padding: 0.75rem;
        vertical-align: top;
        border-top: 1px solid #dee2e6;
      }

      th {
        background-color: #007bff;
        color: #fff;
        border-color: #007bff;
        text-align: left;
      }

      tbody tr:nth-of-type(even) {
        background-color: #f8f9fa;
      }

  </style>
</head>
<body>
  <div class="button-group">
    <a routerLink="/create-supplier" class="button">Tạo mới khách hàng</a>
    <a routerLink="/create-category" class="button">Thêm danh mục</a>
    <a routerLink="/create-category" class="button">In dữ liệu</a>


  </div>
  <table>
    <thead>
      <tr>
        <th>#</th>
        <th>Tên sản phẩm</th>
        <th>Giá</th>
        <th>Số lượng</th>
        <th>Tổng tiền</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of products; let i = index">
        <td>{{ i + 1 }}</td>
        <td>{{ product.name }}</td>
        <td>{{ product.price | currency }}</td>
        <td>{{ product.quantity }}</td>
        <td>{{ product.price * product.quantity | currency }}</td>
      </tr>
    </tbody>
  </table>
<body>
<html>
