import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  watchTypes: WatchType[];

  constructor(private watchTypeService: WatchTypeService) { }

  ngOnInit() {
    this.getWatchTypes();
  }

  getWatchTypes() {
    this.watchTypeService.getWatchTypes()
      .subscribe(
        data => this.watchTypes = data,
        error => console.log(error)
      );
  }

  addWatchType(watchType: WatchType) {
    this.watchTypeService.addWatchType(watchType)
      .subscribe(
        data => {
          this.watchTypes.push(data);
        },
        error => console.log(error)
      );
}
